mapper.select:
SELECT A.JOB_CODE , A.JOB_NAME , (SELECT CC_NAME FROM COM_CODE WHERE CC_ID = A.JOB_LCL) AS JOB_LCL , (SELECT CC_NAME FROM COM_CODE WHERE CC_ID = A.JOB_MCL) AS JOB_MCL , A.JOB_SAL , A.JOB_SATIS , A.JOB_WAY , A.JOB_RELATED_MAJOR , A.JOB_RELATED_CERT , A.JOB_MAIN_DUTY , A.JOB_CREATED_AT , A.FILE_GROUP_NO , A.EDUBG_MGRADU_UNDR , A.EDUBG_HGRADU , A.EDUBG_CGRADU_UNDR , A.EDUBG_UGRADU , A.EDUBG_GGRADU , A.EDUBG_DGRADU , A.OUTLOOK_INCREASE , A.OUTLOOK_SLIGHT_INCREASE , A.OUTLOOK_STABLE , A.OUTLOOK_SLIGHT_DECREASE , A.OUTLOOK_DECREASE , A.JOB_TARGET_ID , B.JR_CODE
, ( SELECT BM_TARGET_ID FROM BOOKMARK WHERE BM_CATEGORY_ID = 'G03004' AND MEM_ID = #{memId} AND BM_TARGET_ID = A.JOB_TARGET_ID) AS IS_BOOKMARK
, ( SELECT JOB_NAME FROM JOBS WHERE JOB_CODE = B.JR_CODE ) AS RELATED_JOB_NAME FROM JOBS A LEFT JOIN JOBS_REL B ON A.JOB_CODE = B.JOB_CODE WHERE A.JOB_CODE = #{jobCode}

mapper.insert:
    	MERGE
		INTO
			JOBS A
				USING DUAL
			ON (A.JOB_CODE = #{jobCode})
			WHEN MATCHED THEN
		UPDATE
		SET
		    JOB_NAME                  = #{jobName}
		    , JOB_LCL                 = #{jobLcl}
		    , JOB_MCL                 = #{jobMcl}
		    , JOB_SAL                 = #{jobSal}
		    , JOB_SATIS               = #{jobSatis}
		    , JOB_WAY                 = #{jobWay}
		    , JOB_RELATED_MAJOR       = #{jobRelatedMajor}
		    , JOB_RELATED_CERT        = #{jobRelatedCert}
		    , JOB_MAIN_DUTY           = #{jobMainDuty}
		    , FILE_GROUP_NO           = #{fileGroupNo}
		    , EDUBG_MGRADU_UNDR       = #{edubgMgraduUndr}
		    , EDUBG_HGRADU            = #{edubgHgradu}
		    , EDUBG_CGRADU_UNDR       = #{edubgCgraduUndr}
		    , EDUBG_UGRADU            = #{edubgUgradu}
		    , EDUBG_GGRADU            = #{edubgGgradu}
		    , EDUBG_DGRADU            = #{edubgDgradu}
		    , OUTLOOK_INCREASE        = #{outlookIncrease}
		    , OUTLOOK_SLIGHT_INCREASE = #{outlookSlightIncrease}
		    , OUTLOOK_STABLE          = #{outlookStable}
		    , OUTLOOK_SLIGHT_DECREASE = #{outlookSlightDecrease}
		    , OUTLOOK_DECREASE        = #{outlookDecrease}
			WHEN NOT MATCHED THEN
		INSERT
			(
				JOB_CODE
				, JOB_NAME
				, JOB_LCL
				, JOB_MCL
				, JOB_SAL
				, JOB_SATIS
				, JOB_WAY
				, JOB_RELATED_MAJOR
				, JOB_RELATED_CERT
				, JOB_MAIN_DUTY
				, JOB_CREATED_AT
				, FILE_GROUP_NO
				, EDUBG_MGRADU_UNDR
				, EDUBG_HGRADU
				, EDUBG_CGRADU_UNDR
				, EDUBG_UGRADU
				, EDUBG_GGRADU
				, EDUBG_DGRADU
				, OUTLOOK_INCREASE
				, OUTLOOK_SLIGHT_INCREASE
				, OUTLOOK_STABLE
				, OUTLOOK_SLIGHT_DECREASE
				, OUTLOOK_DECREASE
				, JOB_TARGET_ID
			)
		VALUES
			(
				#{jobCode}
			  , #{jobName}
			  , #{jobLcl}
			  , #{jobMcl}
			  , #{jobSal}
			  , #{jobSatis}
			  , #{jobWay}
			  , #{jobRelatedMajor}
			  , #{jobRelatedCert}
			  , #{jobMainDuty}
			  , SYSDATE
			  , #{fileGroupNo}
			  , #{edubgMgraduUndr}
			  , #{edubgHgradu}
			  , #{edubgCgraduUndr}
			  , #{edubgUgradu}
			  , #{edubgGgradu}
			  , #{edubgDgradu}
			  , #{outlookIncrease}
			  , #{outlookSlightIncrease}
			  , #{outlookStable}
			  , #{outlookSlightDecrease}
			  , #{outlookDecrease}
			  , (SELECT NVL(MAX(JOB_CODE), 0)+1 FROM JOBS)
			 )
