mapper.update:

      SELECT NVL(MAX(IR_ID), 0) + 1 AS IR_ID FROM INTERVIEW_REVIEW


	MERGE INTO
		INTERVIEW_REVIEW A
	USING DUAL
	ON (IR_ID = #{irId})
		WHEN MATCHED THEN
			UPDATE SET
				A.IR_CONTENT = #{irContent}
				, A.IR_RATING = #{irRating}
				, A.IR_MOD_AT = SYSDATE
		WHEN NOT MATCHED THEN
			INSERT(
			    IR_ID
			    , MEM_ID
			    , IR_TYPE
			    , TARGET_ID
			    , IR_CONTENT
			    , IR_CREATED_AT
			    , IR_MOD_AT
			    , IR_RATING
			    , IR_APPLICATION
			    , IR_INTERVIEW_AT
			    , IR_STATUS
			) VALUES (
				#{irId}
				, #{memId}
				, #{irType}
				, #{targetId}
				, #{irContent}
				, SYSDATE
				, SYSDATE
				, #{irRating}
				, #{irApplication}
				, #{irInterviewAt}
				, 'S06002'
			)

MERGE INTO
	VERIFICATION A
USING DUAL
ON (VERI_ID = #{veriId})
	WHEN MATCHED THEN
		UPDATE SET
			A.FILE_GROUP_ID = #{fileGroupId}
			, A.VERI_STATUS = #{veriStatus}
			, A.VERI_REASON = #{veriReason}
	WHEN NOT MATCHED THEN
		INSERT(
		    VERI_ID
		    , MEM_ID
		    , VERI_CREATED_AT
		    , FILE_GROUP_ID
		    , VERI_STATUS
		    , VERI_CATEGORY
		) VALUES (
			#{veriId}
			, #{memId}
		    , SYSDATE
		    , #{fileGroupId}
		    , #{veriStatus}
		    , #{veriCategory}
		)
