mapper.select:
SELECT COUNT(*) FROM ${tableName} WHERE MEM_ID = #{memId}
AND BM_CATEGORY_ID = 'G03004'
AND SI_STATUS = '완료'

mapper.update:
UPDATE
   MEMBER
SET
   MEM_POINT = MEM_POINT + #{point}
WHERE
   MEM_ID = #{memId}

UPDATE
	ROADMAP
SET
	DUE_DATE = #{dueDate}
WHERE
	MEM_ID = #{memId}
AND
	RS_ID = #{rsId}
