mapper.select:
WITH base AS ( SELECT r.report_id , r.target_type , r.target_id , r.report_reason , r.report_status , r.report_created_at , r.report_complete_at , r.report_complete_id , r.mem_id -- 신고자 ID , m1.mem_name AS reporter_name -- 신고자명 , r.file_group_no , CASE WHEN r.target_type = 'G10001' THEN b.mem_id WHEN r.target_type = 'G10002' THEN rp.mem_id END AS reported_mem_id -- 신고대상자 ID , m2.mem_name AS reported_name -- 신고대상자명 FROM report r /* 신고 대상(게시글/댓글)에 따라 조건부 조인 */ LEFT JOIN board b ON r.target_type = 'G10001' AND r.target_id = b.board_id LEFT JOIN reply rp ON r.target_type = 'G10002' AND r.target_id = rp.reply_id LEFT JOIN member m1 ON r.mem_id = m1.mem_id LEFT JOIN member m2 ON ( CASE WHEN r.target_type = 'G10001' THEN b.mem_id WHEN r.target_type = 'G10002' THEN rp.mem_id END ) = m2.mem_id WHERE r.report_id IS NOT NULL ) , filtered AS ( SELECT t.* , ROW_NUMBER() OVER ( ORDER BY CASE WHEN t.REPORT_STATUS = 'S03001' THEN 0 ELSE 1 END ASC, /* --- 각 그룹 내에서 정렬 컬럼 화이트리스트 --- */
t.REPORT_STATUS ${sortOrder} , t.REPORT_CREATED_AT desc t.MEM_ID ${sortOrder} t.REPORTED_NAME ${sortOrder} t.REPORT_CREATED_AT ${sortOrder} t.REPORT_STATUS ${sortOrder}, t.REPORT_CREATED_AT desc
) AS RNUM FROM base t WHERE 1=1
AND t.REPORT_STATUS = #{filter}
AND t.REPORTER_NAME LIKE '%' || #{keyword} || '%'
AND t.REPORTED_NAME LIKE '%' || #{keyword} || '%'
AND ( t.REPORTER_NAME LIKE '%' || #{keyword} || '%' OR t.REPORTED_NAME LIKE '%' || #{keyword} || '%' OR t.REPORT_REASON LIKE '%' || #{keyword} || '%' )
) SELECT REPORT_ID , TARGET_TYPE , TARGET_ID , REPORT_REASON , REPORT_STATUS , REPORT_CREATED_AT , REPORT_COMPLETE_AT , REPORT_COMPLETE_ID , MEM_ID , REPORTER_NAME , FILE_GROUP_NO , REPORTED_MEM_ID , REPORTED_NAME , RNUM FROM filtered WHERE RNUM BETWEEN #{startNo} AND #{endNo}

mapper.update:
UPDATE REPORT
SET
REPORT_STATUS = #{reportStatus}
WHERE
REPORT_ID = #{reportId}

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RNUM, B.* FROM ( SELECT BOARD_ID, MEM_ID, CC_ID, BOARD_TITLE, BOARD_CONTENT, BOARD_CREATED_AT, BOARD_UPDATED_AT, BOARD_CNT, BOARD_DEL_YN, FILEGROUP_ID FROM BOARD WHERE MEM_ID = #{memId}
AND CC_ID = #{ccId}
ORDER BY
BOARD_ID BOARD_DEL_YN BOARD_CREATED_AT BOARD_ID
DESC ASC
ORDER BY BOARD_ID ASC
) B WHERE ROWNUM <= #{endNo} ) WHERE RNUM >= #{startNo}

SELECT COUNT(*)
FROM APTITUDE_TEST
WHERE MEM_ID = #{memId}

SELECT * FROM ( SELECT ROWNUM AS RNUM, B.* FROM ( SELECT A.REPLY_ID, A.BOARD_ID, A.MEM_ID, A.REPLY_CONTENT, A.REPLY_CREATED_AT, A.REPLY_UPDATED_AT, A.REPLY_DEL_YN, A.REPLY_PARENT_ID, B.CC_ID FROM REPLY A INNER JOIN BOARD B ON A.BOARD_ID = B.BOARD_ID WHERE A.MEM_ID = #{memId}
ORDER BY
A.REPLY_ID A.REPLY_DEL_YN A.REPLY_CREATED_AT A.REPLY_ID
DESC ASC
ORDER BY A.REPLY_ID ASC
) B WHERE ROWNUM <= #{endNo} ) WHERE RNUM >= #{startNo}

SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT PL.PL_ID, PL.MEM_ID, PL.PL_URL, PL.PL_TITLE, PL.PL_REFERER_URL, PL.PL_CREATED_AT, M.MEM_NAME FROM PAGE_LOG PL LEFT JOIN MEMBER M ON PL.MEM_ID = M.MEM_ID
PL.MEM_ID = #{memId}
AND ( PL.PL_TITLE LIKE '%' || #{keyword} || '%' OR PL.PL_URL LIKE '%' || #{keyword} || '%' OR M.MEM_NAME LIKE '%' || #{keyword} || '%' )
ORDER BY
PL.PL_ID PL.MEM_ID M.MEM_NAME PL.PL_TITLE PL.PL_CREATED_AT PL.PL_ID
DESC ASC
ORDER BY PL.PL_CREATED_AT DESC
) A WHERE ROWNUM <= #{endNo} ) WHERE RNUM >= #{startNo}

SELECT COUNT(*) FROM PAGE_LOG PL LEFT JOIN MEMBER M ON PL.MEM_ID = M.MEM_ID
PL.MEM_ID = #{memId}
AND ( PL.PL_TITLE LIKE '%' || #{keyword} || '%' OR PL.PL_URL LIKE '%' || #{keyword} || '%' OR M.MEM_NAME LIKE '%' || #{keyword} || '%' )

mapper.update:
UPDATE	MEMBER_PENALTY
SET		MP_TYPE = #{mpType}
		,MP_WARN_DATE = SYSDATE

		, MP_STARTED_AT = #{penaltyStart}
		, MP_COMPLETE_AT = #{penaltyEnd}


		, MP_STARTED_AT = null
		, MP_COMPLETE_AT = null

WHERE  MP_ID = #{mpId}

UPDATE	MEMBER
SET		DEL_YN = 'N'
WHERE	MEM_ID = #{memId}

UPDATE REPORT
SET REPORT_STATUS = 'S03001'
WHERE REPORT_ID = #{reportId}
