mapper.select:
SELECT COUNT(*) FROM UNIV_DEPT_DETAIL UD LEFT JOIN COM_CODE CC2 ON UD.UDD_M_CLASS = CC2.CC_ID
AND COALESCE(CC2.CC_NAME, UD.UDD_M_CLASS) LIKE '%' || #{keyword} || '%'
AND (
UD.UDD_L_CLASS = #{id}
)

SELECT BM_ID, MEM_ID, BM_CATEGORY_ID, BM_TARGET_ID
FROM BOOKMARK
WHERE MEM_ID = #{memId}
AND BM_CATEGORY_ID = #{bmCategoryId}
